#include 
#include 

namespace bacc = boost::accumulators;

void test1()
{
    int i, j;
    typedef int16_t mytype;
    bacc::accumulator_set<mytype, bacc::stats > bm;
    for(i = 0; i < 100; i++) {
	for(j = 0; j < 99; j++) {
	    bm(10000);
	}
	std::cout << i << '%\n';
    }
    std::cout << bacc::mean(bm) << '\n';
}

void test2()
{
    int i;
    typedef char mytype;
    bacc::accumulator_set<mytype, bacc::stats > bm;
    for(i = 0; i < 100; i++) {
	bm(i);
	std::cout << 'bm: ' << bacc::mean(bm) << '\n';
    }
}
void test3()
{
    int i;
    typedef int64_t mytype;
    bacc::accumulator_set<mytype, bacc::stats > bm;
    for(i = 0; i < 100; i++) {
	bm(i);
	std::cout << 'bm: ' << bacc::mean(bm) << '\n';
    }
}

int main()
{
    std::cout << 'TEST 1\n';
